// app/api/dev-review/route.ts
import { NextResponse } from "next/server";
import OpenAI from "openai";

export const runtime = "nodejs"; // OpenAI SDK-—Ç –∏–ª“Ø“Ø –Ω–∞–π–¥–≤–∞—Ä—Ç–∞–π

export async function POST(req: Request) {
  try {
    const body = await req.json();
    const { code, instruction } = body || {};

    if (!code || !instruction) {
      return NextResponse.json(
        { error: "code –±–æ–ª–æ–Ω instruction —Ö–æ—ë—É–ª –∑–∞–∞–≤–∞–ª —Ö—ç—Ä—ç–≥—Ç—ç–π." },
        { status: 400 }
      );
    }

    // API key –±–∞–π—Ö–≥“Ø–π “Ø–µ–¥ —à—É—É–¥ –æ–π–ª–≥–æ–º–∂—Ç–æ–π —Ö–∞—Ä–∏—É ”©–≥”©—Ö (–∞–ª–¥–∞–∞ —Ü–∞—Ü–∞—Ö–≥“Ø–π)
    if (!process.env.OPENAI_API_KEY) {
      return NextResponse.json(
        {
          result:
            "OPENAI_API_KEY —Ç–æ—Ö–∏—Ä—É—É–ª–∞–≥–¥–∞–∞–≥“Ø–π –±–∞–π–Ω–∞. .env.local –¥–æ—Ç–æ—Ä OPENAI_API_KEY=... –≥—ç–∂ –Ω—ç–º—ç—ç–¥ dev —Å–µ—Ä–≤–µ—Ä—ç—ç –¥–∞—Ö–∏–Ω –∞—Å–∞–∞–Ω–∞ —É—É.",
        },
        { status: 200 }
      );
    }

    const client = new OpenAI({
      apiKey: process.env.OPENAI_API_KEY!,
    });

    const response = await client.chat.completions.create({
      model: "gpt-4.1-mini",
      temperature: 0.2,
      messages: [
        {
          role: "system",
          content:
            "–¢–∞ —Ç—É—Ä—à–ª–∞–≥–∞—Ç–∞–π full-stack –∏–Ω–∂–µ–Ω–µ—Ä. ”®–≥”©–≥–¥—Å”©–Ω –∫–æ–¥—ã–≥ —ç–≤–¥—ç–ª–≥“Ø–π–≥—ç—ç—Ä –∞–ª–¥–∞–∞, —Å–∞–π–∂—Ä—É—É–ª–∞–ª—Ç, —Ä–µ—Ñ–∞–∫—Ç–æ—Ä, –∞—Ä—Ö–∏—Ç–µ–∫—Ç—É—Ä—ã–Ω –∑”©–≤–ª”©–≥”©”© ”©–≥–Ω”©.",
        },
        {
          role: "user",
          content: `
=== –ê–°–£–£–õ–¢ / –ó–ê–ê–í–ê–† ===
${instruction}

=== –ö–û–î ===
${code}
          `,
        },
      ],
    });

    const result = response.choices?.[0]?.message?.content || "No response";

    return NextResponse.json({ result }, { status: 200 });
  } catch (err) {
    console.error("DEV-REVIEW ERROR:", err);
    return NextResponse.json(
      { error: "–°–µ—Ä–≤–µ—Ä–∏–π–Ω –∞–ª–¥–∞–∞ (dev-review route)." },
      { status: 500 }
    );
  }
}

"use client";

import { useState } from "react";
import projectsData from "@/data/projects.json";

// projects.json –¥–æ—Ç–æ—Ä—Ö –Ω—ç–≥ —Ñ–∞–π–ª—ã–Ω –±“Ø—Ç—ç—Ü
type ProjectFile = {
  path: string;
  content: string;
  summary?: string;
};

// projects.json –±“Ø—Ö—ç–ª–¥—ç—ç
type ProjectsMap = {
  [project: string]: {
    files: ProjectFile[];
  };
};

// JSON-—ã–≥ type-—Ç–∞–π –±–æ–ª–≥–æ—Ö
const projects = projectsData as ProjectsMap;

export default function DevConsolePage() {
  // –û–¥–æ–æ–≥–æ–æ—Ä –∑”©–≤—Ö”©–Ω "income-app" —Ç”©—Å–ª–∏–π–≥ —É–Ω—à–∏–Ω–∞
  const files: ProjectFile[] = projects["income-app"]?.files ?? [];

  const [selectedPath, setSelectedPath] = useState("");
  const [code, setCode] = useState("");
  const [instruction, setInstruction] = useState("");
  const [result, setResult] = useState("");
  const [loading, setLoading] = useState(false);

  // –§–∞–π–ª —Å–æ–Ω–≥–æ—Ö–æ–¥ –∫–æ–¥—ã–≥ –∞–≤—Ç–æ–º–∞—Ç–∞–∞—Ä —Ö–∞—Ä—É—É–ª–∞—Ö
  function handleSelect(path: string) {
    setSelectedPath(path);

    const f = files.find((x) => x.path === path);
    if (f) {
      setCode(f.content);
    } else {
      setCode("");
    }
  }

  // Review API —Ä—É—É –∏–ª–≥—ç—ç—Ö
  async function runReview() {
    if (!instruction.trim()) {
      alert("Instruction —Ö–æ–æ—Å–æ–Ω –±–∞–π–Ω–∞!");
      return;
    }

    if (!code.trim()) {
      alert("–ö–æ–¥ —Ö–æ–æ—Å–æ–Ω –±–∞–π–Ω–∞!");
      return;
    }

    setLoading(true);
    setResult("");

    try {
      const res = await fetch("/api/dev-review", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          code,
          instruction,
        }),
      });

      const json = await res.json();
      setResult(json.result || json.error || "No response");
    } catch (err: any) {
      setResult("–•–æ–ª–±–æ–ª—Ç—ã–Ω –∞–ª–¥–∞–∞: " + (err?.message ?? "unknown"));
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="p-6 space-y-6 max-w-5xl mx-auto">
      <h1 className="text-2xl font-bold">AI Dev Console V1</h1>
      <p className="text-sm text-gray-600">
        income-app —Ç”©—Å–ª–∏–π–Ω —Ñ–∞–π–ª–∞–∞ —Å–æ–Ω–≥–æ–æ–¥, AI-–∞–∞—Å –∫–æ–¥—ã–Ω –∞–Ω–∞–ª–∏–∑, —Å–∞–π–∂—Ä—É—É–ª–∞–ª—Ç—ã–Ω
        —Å–∞–Ω–∞–ª –∞–≤–Ω–∞.
      </p>

      {/* –§–ê–ô–õ –°–û–ù–ì–û–ì–ß */}
      <div>
        <label className="font-semibold">–§–∞–π–ª —Å–æ–Ω–≥–æ—Ö</label>
        <select
          className="border p-2 rounded w-full mt-1"
          value={selectedPath}
          onChange={(e) => handleSelect(e.target.value)}
        >
          <option value="">-- –§–∞–π–ª —Å–æ–Ω–≥–æ --</option>
          {files.map((f) => (
            <option key={f.path} value={f.path}>
              {f.path}
            </option>
          ))}
        </select>
      </div>

      {/* INSTRUCTION */}
      <div>
        <label className="font-semibold">Instruction</label>
        <textarea
          className="border p-2 rounded w-full h-24 mt-1"
          placeholder='–ñ: "–ú–∞—Ä–≥–∞–∞–Ω—ã —Ç–∞–± –¥—ç—ç—Ä notification badge –Ω—ç–º, –ª–æ–≥–∏–∫–∏–π–≥ —Ç–∞–π–ª–±–∞—Ä–ª–∞"'
          value={instruction}
          onChange={(e) => setInstruction(e.target.value)}
        />
      </div>

      {/* CODE VIEW */}
      <div>
        <label className="font-semibold">–ö–æ–¥ (—É–Ω—à–∏—Ö —Ö—ç—Å—ç–≥)</label>
        <textarea
          className="border p-2 rounded w-full h-64 mt-1 font-mono text-sm"
          value={code}
          onChange={(e) => setCode(e.target.value)}
        />
      </div>

      {/* RUN BUTTON */}
      <button
        onClick={runReview}
        disabled={loading}
        className="bg-blue-600 text-white px-4 py-2 rounded disabled:opacity-60"
      >
        {loading ? "–ê–Ω–∞–ª–∏–∑ —Ö–∏–π–∂ –±–∞–π–Ω–∞..." : "AI Review –∞–∂–∏–ª–ª—É—É–ª–∞—Ö"}
      </button>

      {/* RESULT */}
      <div>
        <label className="font-semibold">“Æ—Ä –¥“Ø–Ω</label>
        <textarea
          className="border p-2 rounded w-full h-64 mt-1 bg-gray-50 text-sm"
          value={result}
          readOnly
        />
      </div>
    </div>
  );
}

"use client";

// =================== 1. –ò–º–ø–æ—Ä—Ç, —Ç”©—Ä–ª“Ø“Ø–¥ ===================

import { useEffect, useState } from "react";
import { useRouter, useParams, useSearchParams } from "next/navigation";
import { supabase } from "@/lib/supabase";

type Role = "seller" | "driver";

type IncomeUser = {
  id: string;
  role: Role;
  name: string;
  phone: string;
  email: string;
};

type DeliveryStatus =
  | "OPEN"
  | "ASSIGNED"
  | "PICKED_UP"
  | "DELIVERED"
  | "RETURNED"
  | "CLOSED"
  | "CANCELLED"
  | "DISPUTE";

type DeliveryDetail = {
  id: string;
  seller_id: string;
  from_address: string | null;
  to_address: string | null;
  note: string | null;
  status: DeliveryStatus;
  created_at: string;
  price_mnt: number | null;
  delivery_type: string | null;
  chosen_driver_id: string | null;

  seller_marked_paid: boolean;
  driver_confirmed_payment: boolean;
  closed_at: string | null;

  // –ë—É—Ü–∞–∞–ª—Ç—Ç–∞–π —Ö–æ–ª–±–æ–æ—Ç–æ–π flag
  return_rejected_by_driver: boolean;

  // seller-–∏–π–Ω –±–æ–≥–∏–Ω–æ info
  seller_name?: string | null;
  seller_phone?: string | null;
};

type DriverBidRow = {
  id: string;
  delivery_id: string;
  driver_id: string;
  created_at: string;
};

type DriverOwnBid = DriverBidRow | null;

// =================== 2. –¢—É—Å–ª–∞—Ö —Ñ—É–Ω–∫—Ü—É—É–¥ ===================

function typeLabel(
  deliveryType: string | null
): { icon: string; label: string } {
  switch (deliveryType) {
    case "apartment":
      return { icon: "üèô", label: "–ë–∞–π—Ä" };
    case "ger":
      return { icon: "üè†", label: "–ì—ç—Ä —Ö–æ—Ä–æ–æ–ª–æ–ª" };
    case "camp":
      return { icon: "üèï", label: "–õ–∞–≥–µ—Ä" };
    case "countryside":
      return { icon: "üöå", label: "–û—Ä–æ–Ω –Ω—É—Ç–∞–≥" };
    default:
      return { icon: "üì¶", label: "–•“Ø—Ä–≥—ç–ª—Ç" };
  }
}

function statusBadge(status: DeliveryStatus) {
  switch (status) {
    case "OPEN":
      return {
        text: "–ù—ç—ç–ª—Ç—Ç—ç–π",
        className: "bg-emerald-50 text-emerald-700 border-emerald-100",
      };
    case "ASSIGNED":
      return {
        text: "–¢–∞–Ω–¥ –æ–Ω–æ–æ—Å–æ–Ω",
        className: "bg-sky-50 text-sky-700 border-sky-100",
      };
    case "PICKED_UP":
      return {
        text: "–ó–∞–º–¥",
        className: "bg-indigo-50 text-indigo-700 border-indigo-100",
      };
    case "DELIVERED":
      return {
        text: "–•“Ø—Ä–≥—ç—Å—ç–Ω",
        className: "bg-slate-900 text-white border-slate-900",
      };
    case "RETURNED":
      return {
        text: "–ë—É—Ü–∞–∞—Å–∞–Ω",
        className: "bg-amber-50 text-amber-800 border-amber-100",
      };
    case "CLOSED":
      return {
        text: "–•–∞–∞–≥–¥—Å–∞–Ω",
        className: "bg-emerald-900 text-emerald-50 border-emerald-900",
      };
    case "CANCELLED":
      return {
        text: "–¶—É—Ü–∞–ª—Å–∞–Ω",
        className: "bg-rose-50 text-rose-700 border-rose-100",
      };
    case "DISPUTE":
      return {
        text: "–ú–∞—Ä–≥–∞–∞–Ω",
        className: "bg-rose-50 text-rose-700 border-rose-100",
      };
    default:
      return {
        text: status,
        className: "bg-slate-50 text-slate-600 border-slate-100",
      };
  }
}

function shorten(s: string | null, max = 120) {
  if (!s) return "";
  const t = s.trim();
  if (t.length <= max) return t;
  return t.slice(0, max).replace(/\s+$/, "") + "‚Ä¶";
}

function formatPrice(n: number | null) {
  if (!n) return "“Æ–Ω—ç —Ç–æ—Ö–∏—Ä–æ–ª—Ü–æ–Ω–æ";
  return n.toLocaleString("mn-MN") + "‚ÇÆ";
}

function formatDateTime(iso: string | null | undefined) {
  if (!iso) return "";
  const d = new Date(iso);
  return (
    d.toLocaleDateString("mn-MN", { month: "2-digit", day: "2-digit" }) +
    " " +
    d.toLocaleTimeString("mn-MN", { hour: "2-digit", minute: "2-digit" })
  );
}

function mapsUrl(addr: string | null) {
  if (!addr) return "";
  const q = encodeURIComponent(addr);
  return `https://maps.google.com/?q=${q}`;
}

// –ú–∞—Ä–≥–∞–∞–Ω –Ω—ç—ç–∂ –±–æ–ª–æ—Ö —Å—Ç–∞—Ç—É—Å—Ç–∞–π —ç—Å—ç—Ö (driver —Ç–∞–ª)
function canOpenDisputeForDriver(
  status: DeliveryStatus,
  isThisDriverAssigned: boolean
): boolean {
  if (!isThisDriverAssigned) return false;
  if (status === "DISPUTE" || status === "CLOSED" || status === "CANCELLED")
    return false;

  // ASSIGNED / PICKED_UP / DELIVERED / RETURNED “Ø–µ–¥ –ª –º–∞—Ä–≥–∞–∞–Ω –Ω—ç—ç—Ö
  return (
    status === "ASSIGNED" ||
    status === "PICKED_UP" ||
    status === "DELIVERED" ||
    status === "RETURNED"
  );
}

// =================== 3. –ì–æ–ª –∫–æ–º–ø–æ–Ω–µ–Ω—Ç ===================

export default function DriverDeliveryDetailPage() {
  const router = useRouter();
  const params = useParams();
  const searchParams = useSearchParams();

  const idParam = (params as any)?.id;
  const deliveryId =
    typeof idParam === "string"
      ? idParam
      : Array.isArray(idParam)
      ? idParam[0]
      : "";

  // –•—ç—Ä—ç–≤ driver page –¥—ç—ç—Ä —Ç–∞–±—Ç–∞–π –±–æ–ª ?tab=ACTIVE –≥—ç—Ö –º—ç—Ç–∏–π–≥ —É–Ω—à–∏–∂ –±—É—Ü–Ω–∞
  const fromTab = searchParams.get("tab");
  const backUrl = fromTab ? `/driver?tab=${fromTab}` : "/driver";

  // ---- —Ç”©–ª”©–≤“Ø“Ø–¥ ----
  const [user, setUser] = useState<IncomeUser | null>(null);
  const [loadingUser, setLoadingUser] = useState(true);

  const [delivery, setDelivery] = useState<DeliveryDetail | null>(null);
  const [loadingDetail, setLoadingDetail] = useState(true);

  const [ownBid, setOwnBid] = useState<DriverOwnBid>(null);
  const [loadingBid, setLoadingBid] = useState(true);

  // action-—É—É–¥—ã–Ω loading
  const [requesting, setRequesting] = useState(false);
  const [markingPickedUp, setMarkingPickedUp] = useState(false);
  const [markingDelivered, setMarkingDelivered] = useState(false);
  const [markingReturned, setMarkingReturned] = useState(false);
  const [confirmPayLoading, setConfirmPayLoading] = useState(false);

  // –ú–∞—Ä–≥–∞–∞–Ω
  const [showDisputeModal, setShowDisputeModal] = useState(false);
  const [disputeReason, setDisputeReason] = useState("");
  const [openingDispute, setOpeningDispute] = useState(false);

  const [message, setMessage] = useState<string | null>(null);
  const [error, setError] = useState<string | null>(null);

  // =================== 4. Login guard ===================

  useEffect(() => {
    try {
      const raw = window.localStorage.getItem("incomeUser");
      if (!raw) {
        router.replace("/");
        return;
      }
      const parsed: IncomeUser = JSON.parse(raw);
      if (parsed.role !== "driver") {
        router.replace("/");
        return;
      }
      setUser(parsed);
      setLoadingUser(false);
    } catch (e) {
      console.error(e);
      setError("–•—ç—Ä—ç–≥–ª—ç–≥—á–∏–π–Ω –º—ç–¥—ç—ç–ª—ç–ª —É–Ω—à–∏—Ö–∞–¥ –∞–ª–¥–∞–∞ –≥–∞—Ä–ª–∞–∞.");
      setLoadingUser(false);
    }
  }, [router]);

  // =================== 5. –•“Ø—Ä–≥—ç–ª—Ç + ”©”©—Ä–∏–π–Ω bid —Ç–∞—Ç–∞—Ö ===================

  useEffect(() => {
    if (!user || !deliveryId) return;
    void fetchDetailAndBid(user.id, deliveryId);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [user, deliveryId]);

  async function fetchDetailAndBid(driverId: string, id: string) {
    try {
      setLoadingDetail(true);
      setLoadingBid(true);
      setError(null);
      setMessage(null);

      // 5.1 –•“Ø—Ä–≥—ç–ª—Ç–∏–π–Ω –¥—ç–ª–≥—ç—Ä—ç–Ω–≥“Ø–π
      const { data, error } = await supabase
        .from("deliveries")
        .select(
          `
          id,
          seller_id,
          from_address,
          to_address,
          note,
          status,
          created_at,
          price_mnt,
          delivery_type,
          chosen_driver_id,
          seller_marked_paid,
          driver_confirmed_payment,
          closed_at,
          return_rejected_by_driver,
          seller:seller_id (
            name,
            phone
          )
        `
        )
        .eq("id", id)
        .maybeSingle();

      if (error) {
        console.error(error);
        setError("–•“Ø—Ä–≥—ç–ª—Ç–∏–π–Ω –º—ç–¥—ç—ç–ª—ç–ª —Ç–∞—Ç–∞—Ö–∞–¥ –∞–ª–¥–∞–∞ –≥–∞—Ä–ª–∞–∞.");
        setDelivery(null);
      } else if (!data) {
        setError("–ò–π–º —Ö“Ø—Ä–≥—ç–ª—Ç –æ–ª–¥—Å–æ–Ω–≥“Ø–π.");
        setDelivery(null);
      } else {
        const d = data as any;
        const detail: DeliveryDetail = {
          id: d.id,
          seller_id: d.seller_id,
          from_address: d.from_address,
          to_address: d.to_address,
          note: d.note,
          status: d.status,
          created_at: d.created_at,
          price_mnt: d.price_mnt,
          delivery_type: d.delivery_type,
          chosen_driver_id: d.chosen_driver_id,
          seller_marked_paid: !!d.seller_marked_paid,
          driver_confirmed_payment: !!d.driver_confirmed_payment,
          closed_at: d.closed_at,
          return_rejected_by_driver: !!d.return_rejected_by_driver,
          seller_name: d.seller?.name ?? null,
          seller_phone: d.seller?.phone ?? null,
        };
        setDelivery(detail);
      }

      setLoadingDetail(false);

      // 5.2 –≠–Ω—ç —Ö“Ø—Ä–≥—ç–ª—Ç –¥—ç—ç—Ä—Ö ”©”©—Ä–∏–π–Ω bid –±–∞–π–≥–∞–∞ —ç—Å—ç—Ö
      const { data: bidData, error: bidError } = await supabase
        .from("driver_bids")
        .select("id, delivery_id, driver_id, created_at")
        .eq("delivery_id", id)
        .eq("driver_id", driverId)
        .maybeSingle();

      if (bidError && bidError.code !== "PGRST116") {
        console.error("BID LOAD ERROR:", bidError);
      }

      if (!bidError && bidData) {
        setOwnBid(bidData as DriverBidRow);
      } else {
        setOwnBid(null);
      }

      setLoadingBid(false);
    } catch (e) {
      console.error(e);
      setError("–ú—ç–¥—ç—ç–ª—ç–ª —Ç–∞—Ç–∞—Ö —è–≤—Ü–∞–¥ –∞–ª–¥–∞–∞ –≥–∞—Ä–ª–∞–∞.");
      setLoadingDetail(false);
      setLoadingBid(false);
    }
  }

  // =================== 6. –ì–∞—Ä–∞—Ö ===================

  function handleLogout() {
    if (typeof window !== "undefined") {
      window.localStorage.removeItem("incomeUser");
    }
    router.push("/");
  }

  // =================== 7. –ê–≤–∞—Ö —Ö“Ø—Å—ç–ª—Ç –∏–ª–≥—ç—ç—Ö (OPEN “Ø–µ–¥) ===================

  async function handleRequestDelivery() {
    if (!user || !delivery) return;

    if (delivery.status !== "OPEN") {
      setMessage("–ó”©–≤—Ö”©–Ω –Ω—ç—ç–ª—Ç—Ç—ç–π —Ö“Ø—Ä–≥—ç–ª—Ç –¥—ç—ç—Ä –∞–≤–∞—Ö —Ö“Ø—Å—ç–ª—Ç –≥–∞—Ä–≥–∞–∂ –±–æ–ª–Ω–æ.");
      return;
    }

    if (ownBid) {
      setMessage("–¢–∞ –∞–ª—å —Ö—ç–¥–∏–π–Ω —ç–Ω—ç —Ö“Ø—Ä–≥—ç–ª—Ç –¥—ç—ç—Ä –∞–≤–∞—Ö —Ö“Ø—Å—ç–ª—Ç –∏–ª–≥—ç—ç—Å—ç–Ω –±–∞–π–Ω–∞.");
      return;
    }

    setRequesting(true);
    setError(null);
    setMessage(null);

    try {
      const { data, error } = await supabase
        .from("driver_bids")
        .insert({
          delivery_id: delivery.id,
          driver_id: user.id,
        })
        .select()
        .single();

      if (error) {
        console.error(error);
        setError("–ê–≤–∞—Ö —Ö“Ø—Å—ç–ª—Ç –∏–ª–≥—ç—ç—Ö—ç–¥ –∞–ª–¥–∞–∞ –≥–∞—Ä–ª–∞–∞.");
        return;
      }

      setOwnBid(data as DriverBidRow);
      setMessage("–ê–≤–∞—Ö —Ö“Ø—Å—ç–ª—Ç –∞–º–∂–∏–ª—Ç—Ç–∞–π –∏–ª–≥—ç—ç–≥–¥–ª—ç—ç.");
    } finally {
      setRequesting(false);
    }
  }

  // =================== 8. –•“Ø—Ä–≥—ç–ª—Ç–∏–π–Ω —è–≤—Ü—ã–Ω —Å—Ç–∞—Ç—É—Å ”©”©—Ä—á–ª”©—Ö ===================

  async function updateStatus(newStatus: DeliveryStatus) {
    if (!user || !delivery) return;

    setError(null);
    setMessage(null);

