    setMessage(null);

    try {
      const { error } = await supabase
        .from("deliveries")
        .update({ status: "PICKED_UP" })
        .eq("id", delivery.id)
        .eq("seller_id", user.id);

      if (error) {
        console.error(error);
        setError("–•“Ø—Ä–≥—ç–ª—Ç—ç–¥ –≥–∞—Ä—Å–∞–Ω –≥—ç–∂ —Ç—ç–º–¥—ç–≥–ª—ç—Ö—ç–¥ –∞–ª–¥–∞–∞ –≥–∞—Ä–ª–∞–∞.");
