  /* ===========================
   * BLOCK 11 ‚Äî CANCEL DRIVER (ASSIGNED ‚Üí OPEN + –±–ª–æ–∫)
   * =========================== */

  const canCancelDriver =
    !!delivery && delivery.status === "ASSIGNED" && !!delivery.chosen_driver_id;

  function toggleCancelReason(key: keyof typeof cancelReasons) {
