import { useEffect, useState } from "react";
import { useRouter, useSearchParams } from "next/navigation";
import { supabase } from "@/lib/supabase";
import {
  DeliveryStatus,
  SELLER_TABS,
} from "@/lib/deliveryLogic";

/* ===========================
 * BLOCK 2 ‚Äî –¢”®–†–õ“Æ“Æ–î (TYPES)
 * - Role, IncomeUser, DeliveryRow, SellerTabId, TAB_IDS
 * =========================== */

type Role = "seller" | "driver";

type IncomeUser = {
  id: string;
  role: Role;
  name: string;
  phone: string;
  email: string;
};

type DeliveryRow = {
  id: string;
  seller_id: string;
  from_address: string | null;
  to_address: string | null;
  note: string | null;
  status: DeliveryStatus;
  created_at: string;
  price_mnt: number | null;
  delivery_type: string | null;

  seller_marked_paid: boolean | null;
  driver_confirmed_payment: boolean | null;
  closed_at: string | null;
};

// SELLER_TABS-–∏–π–≥ deliveryLogic –¥–æ—Ç–æ—Ä—Ö —Ç–æ–¥–æ—Ä—Ö–æ–π–ª–æ–ª—Ç–æ–æ—Å –Ω—å —à—É—É–¥ –∞—à–∏–≥–ª–∞–∂ –±–∞–π–Ω–∞
type SellerTabId = (typeof SELLER_TABS)[number]["id"];

// URL / localStorage-–¥ —Ö–∞–¥–≥–∞–ª–∞—Ö–¥–∞–∞ –∞—à–∏–≥–ª–∞—Ö —Ç–∞–±—É—É–¥—ã–Ω ID –∂–∞–≥—Å–∞–∞–ª—Ç
const TAB_IDS: SellerTabId[] = SELLER_TABS.map((t) => t.id);

/* ===========================
 * BLOCK 3 ‚Äî –ñ–ò–ñ–ò–ì –¢–£–°–õ–ê–• –§–£–ù–ö–¶–£–£–î
 * - typeLabel, statusBadge, shorten, formatPrice, formatDateTime
 * =========================== */

function typeLabel(deliveryType: string | null): { icon: string; label: string } {
  switch (deliveryType) {
    case "apartment":
      return { icon: "üèô", label: "–ë–∞–π—Ä" };
    case "ger":
      return { icon: "üè†", label: "–ì—ç—Ä —Ö–æ—Ä–æ–æ–ª–æ–ª" };
    case "camp":
      return { icon: "üèï", label: "–õ–∞–≥–µ—Ä" };
    case "countryside":
      return { icon: "üöå", label: "–û—Ä–æ–Ω –Ω—É—Ç–∞–≥" };
    default:
      return { icon: "üì¶", label: "–•“Ø—Ä–≥—ç–ª—Ç" };
  }
}

function statusBadge(status: DeliveryStatus) {
  switch (status) {
    case "OPEN":
      return {
        text: "–ù—ç—ç–ª—Ç—Ç—ç–π",
        className: "bg-emerald-50 text-emerald-700 border-emerald-100",
      };
    case "ASSIGNED":
      return {
        text: "–ñ–æ–ª–æ–æ—á —Å–æ–Ω–≥–æ—Å–æ–Ω",
        className: "bg-sky-50 text-sky-700 border-sky-100",
      };
    case "PICKED_UP":
      return {
        text: "–ó–∞–º–¥",
        className: "bg-indigo-50 text-indigo-700 border-indigo-100",
      };
    case "DELIVERED":
      return {
        text: "–•“Ø—Ä–≥—ç—Å—ç–Ω",
        className: "bg-slate-900 text-white border-slate-900",
      };
    case "RETURNED":
      return {
        text: "–ë—É—Ü–∞–∞–ª—Ç",
        className: "bg-amber-50 text-amber-800 border-amber-100",
      };
    case "DISPUTE":
      return {
        text: "–ú–∞—Ä–≥–∞–∞–Ω",
        className: "bg-rose-50 text-rose-700 border-rose-100",
      };
    case "CANCELLED":
      return {
        text: "–¶—É—Ü–∞–ª—Å–∞–Ω",
        className: "bg-rose-50 text-rose-700 border-rose-100",
      };
    case "CLOSED":
      return {
        text: "–•–∞–∞–≥–¥—Å–∞–Ω",
        className: "bg-emerald-900 text-emerald-50 border-emerald-900",
      };
    default:
      return {
        text: status,
        className: "bg-slate-50 text-slate-600 border-slate-100",
      };
  }
}

function shorten(s: string | null, max = 110) {
  if (!s) return "";
  const t = s.trim();
  if (t.length <= max) return t;
  return t.slice(0, max).replace(/\s+$/, "") + "‚Ä¶";
}

function formatPrice(n: number | null) {
  if (!n) return "“Æ–Ω—ç —Ç–æ—Ö–∏—Ä–æ–ª—Ü–æ–Ω–æ";
  return n.toLocaleString("mn-MN") + "‚ÇÆ";
}

function formatDateTime(iso: string) {
  if (!iso) return "";
  const d = new Date(iso);
  return (
    d.toLocaleDateString("mn-MN", { month: "2-digit", day: "2-digit" }) +
    " " +
    d.toLocaleTimeString("mn-MN", { hour: "2-digit", minute: "2-digit" })
  );
}

/* ===========================
 * BLOCK 4 ‚Äî FILTER –õ–û–ì–ò–ö (–¢–ê–ë –ë“Æ–†–¢–≠–ô –•–û–õ–ë–û–ì–î–û–•)
 * - filterByTab: –Ω—ç–≥ –≥–∞–∑–∞—Ä —Ç”©–≤–ª”©—Ä“Ø“Ø–ª—Å—ç–Ω –¥“Ø—Ä—ç–º
 * =========================== */

function filterByTab(tab: SellerTabId, items: DeliveryRow[]): DeliveryRow[] {
  return items.filter((d) => {
    switch (tab) {
      case "OPEN":
        return d.status === "OPEN";
      case "ASSIGNED":
        return d.status === "ASSIGNED";
      case "PICKED_UP":
        return d.status === "PICKED_UP"; // DISPUTE-–≥ —ç–Ω–¥—ç—ç—Å —Å–∞–ª–≥–∞—Å–∞–Ω
      case "DELIVERED":
        // —Ö“Ø—Ä–≥—ç—Å—ç–Ω –±–æ–ª–æ–≤—á —Ç”©–ª–±”©—Ä —Ç—ç–º–¥—ç–≥–ª—ç—ç–≥“Ø–π
        return d.status === "DELIVERED" && !d.seller_marked_paid;
      case "RETURNED":
        return d.status === "RETURNED";
      case "PAID":
        // —Ö—É–¥–∞–ª–¥–∞–≥—á —Ç”©–ª—Å”©–Ω, –∂–æ–ª–æ–æ—á —Ö–∞—Ä–∞–∞—Ö–∞–Ω –±–∞—Ç–ª–∞–∞–≥“Ø–π (DELIVERED —Ç”©–ª”©–≤)
        return (
          d.status === "DELIVERED" &&
          !!d.seller_marked_paid &&
          !d.driver_confirmed_payment
        );
      case "CLOSED":
        return d.status === "CLOSED";
      case "DISPUTE":
        return d.status === "DISPUTE";
      default:
        return true;
    }
  });
}

/* ===========================
 * BLOCK 5 ‚Äî –ì–û–õ –ö–û–ú–ü–û–ù–ï–ù–¢ (SellerDashboardPage)
 * - Login guard
 * - Tab state
 * - Data fetch
 * - UI render
 * =========================== */

export default function SellerDashboardPage() {
  const router = useRouter();
  const searchParams = useSearchParams();

  // --- state-—É—É–¥ ---
  const [user, setUser] = useState<IncomeUser | null>(null);
  const [loadingUser, setLoadingUser] = useState(true);

  const [deliveries, setDeliveries] = useState<DeliveryRow[]>([]);
