        .maybeSingle();

      if (e1) throw e1;

      if (!data || data.status !== "DELIVERED") {
        setError("–®–∏–ª–∂–∏–ª—Ç –∞–º–∂–∏–ª—Ç–≥“Ø–π. (ON_ROUTE‚ÜíDELIVERED) –î–∞—Ö–∏–Ω –æ—Ä–æ–ª–¥–æ–Ω–æ —É—É.");
        return;
      }

      setItems((prev) => prev.map((x) => (x.id === deliveryId ? { ...x, status: "DELIVERED" as any } : x)));
      changeTab("DELIVERED");
      setMsg("–•“Ø—Ä–≥—ç–ª—Ç–∏–π–≥ —Ö“Ø—Ä–≥—ç—Å—ç–Ω –≥—ç–∂ —Ç—ç–º–¥—ç–≥–ª—ç–ª—ç—ç.");
      void fetchAll(user.id);
    } catch (e: any) {
      console.error(e);
      setError("–®–∏–Ω—ç—á–ª—ç—Ö—ç–¥ –∞–ª–¥–∞–∞ –≥–∞—Ä–ª–∞–∞. –î–∞—Ö–∏–Ω –æ—Ä–æ–ª–¥–æ–Ω–æ —É—É.");
    } finally {
      setActLoading((p) => ({ ...p, [deliveryId]: false }));
    }
  }

  // ‚úÖ PAID ‚Üí CLOSED
  async function confirmPaymentReceived(deliveryId: string) {
    if (!user) return;
    if (actLoading[deliveryId]) return;

    setActLoading((p) => ({ ...p, [deliveryId]: true }));
    setError(null);
    setMsg(null);

    try {
      const cur = items.find((x) => x.id === deliveryId);
      if (!cur) return;

      const ok = canDriverConfirmPayment({
        status: cur.status,
        driver_confirmed_payment: !!cur.driver_confirmed_payment,
      });

      const isMine = cur.chosen_driver_id === user.id;

      if (!ok || !isMine) {
        setError("–ó”©–≤—Ö”©–Ω '–¢”©–ª—Å”©–Ω' “Ø–µ–¥ —Ç”©–ª–±”©—Ä —Ö“Ø–ª—ç—ç–Ω –∞–≤—Å–Ω–∞–∞ –±–∞—Ç–∞–ª–Ω–∞.");
        return;
      }

      const closedAt = new Date().toISOString();

      const { data, error: e1 } = await supabase
        .from("deliveries")
        .update({
          driver_confirmed_payment: true,
          status: "CLOSED",
          closed_at: closedAt,
        })
        .eq("id", deliveryId)
        .eq("chosen_driver_id", user.id)
        .eq("status", "PAID")
        .eq("driver_confirmed_payment", false)
        .select("id,status,driver_confirmed_payment,closed_at")
        .maybeSingle();

      if (e1) throw e1;

      if (!data || data.status !== "CLOSED") {
        setError("–®–∏–ª–∂–∏–ª—Ç –∞–º–∂–∏–ª—Ç–≥“Ø–π. (PAID‚ÜíCLOSED) –î–∞—Ö–∏–Ω –æ—Ä–æ–ª–¥–æ–Ω–æ —É—É.");
        return;
      }

      setItems((prev) =>
        prev.map((x) =>
          x.id === deliveryId
            ? {
                ...x,
                status: "CLOSED" as any,
                driver_confirmed_payment: true,
                closed_at: (data as any).closed_at ?? closedAt,
              }
            : x
        )
      );

      changeTab("CLOSED");
      setMsg("–¢”©–ª–±”©—Ä —Ö“Ø–ª—ç—ç–Ω –∞–≤—Å–Ω–∞–∞ –±–∞—Ç–∞–ª–ª–∞–∞.");
      void fetchAll(user.id);
    } catch (e: any) {
      console.error(e);
      setError("–¢”©–ª–±”©—Ä –±–∞—Ç–ª–∞—Ö–∞–¥ –∞–ª–¥–∞–∞ –≥–∞—Ä–ª–∞–∞.");
    } finally {
      setActLoading((p) => ({ ...p, [deliveryId]: false }));
    }
  }

  // ---------------- UI ----------------

  if (!user) return null;

  return (
    <div className="min-h-screen bg-slate-50">
      <div className="mx-auto max-w-5xl px-4 py-6">
        {/* header */}
        <div className="flex items-center justify-between gap-3">
          <div>
            <div className="text-sm text-slate-500">INCOME</div>
            <h1 className="text-xl font-bold text-slate-900">–ñ–æ–ª–æ–æ—á</h1>
            <div className="text-xs text-slate-500 mt-1">
              {user.name} ¬∑ {user.phone}
            </div>
          </div>

          <div className="flex items-center gap-2">
            <button
              onClick={() => router.push("/driver/profile")}
              className="rounded-xl border border-slate-200 bg-white px-4 py-2 text-sm font-semibold text-slate-900 hover:border-slate-300"
            >
              –ü—Ä–æ—Ñ–∞–π–ª
            </button>
          </div>
        </div>

        {/* tabs */}
        <div className="mt-5 flex flex-wrap gap-2">
          {DRIVER_TABS.map((t) => {
            const active = activeTab === t.id;
