                className={
                  active
                    ? "rounded-full bg-slate-900 text-white px-4 py-2 text-sm font-semibold"
                    : "rounded-full border border-slate-200 bg-white text-slate-700 px-4 py-2 text-sm font-semibold hover:border-slate-300"
                }
              >
                {t.label}
              </button>
