                  placeholder="5700..."
                />
              </div>

              <div>
                <div className="text-[11px] text-slate-600 mb-1">–î–∞–Ω—Å —ç–∑—ç–º—à–∏–≥—á</div>
                <input
                  value={accountHolder}
                  onChange={(e) => setAccountHolder(e.target.value)}
                  className="w-full rounded-xl border border-slate-200 bg-slate-50 px-3 py-2 text-sm text-slate-900 focus:outline-none focus:ring-2 focus:ring-slate-900/10"
                  placeholder="–û–≤–æ–≥ –ù—ç—Ä"
                />
              </div>
            </div>

            <div className="pt-2">
              <button
                onClick={() => void save()}
                disabled={saving}
                className="rounded-xl bg-slate-900 px-4 py-2 text-sm font-semibold text-white hover:bg-slate-800 disabled:opacity-60"
              >
                {saving ? "–•–∞–¥–≥–∞–ª–∂ –±–∞–π–Ω–∞‚Ä¶" : "–•–∞–¥–≥–∞–ª–∞—Ö"}
              </button>
            </div>
          </section>
        )}
      </main>
