  const [isSubmitting, setIsSubmitting] = useState(false);

  const roleLabel = role === "seller" ? "–•—É–¥–∞–ª–¥–∞–≥—á" : "–ñ–æ–ª–æ–æ—á";

  async function handleSubmit(e: FormEvent<HTMLFormElement>) {
    e.preventDefault();
    setError("");

    // --- –§—Ä–æ–Ω—Ç —Ç–∞–ª—ã–Ω —à–∞–ª–≥–∞–ª—Ç ---
    if (!name.trim()) {
      setError("–ù—ç—Ä—ç—ç –±”©–≥–ª”©–Ω”© “Ø“Ø.");
      return;
    }
    if (!phone.trim()) {
      setError("–£—Ç–∞—Å–Ω—ã –¥—É–≥–∞–∞—Ä–∞–∞ –±”©–≥–ª”©–Ω”© “Ø“Ø.");
      return;
    }
    if (!email.trim()) {
      setError("–ò–º—ç–π–ª —Ö–∞—è–≥–∞–∞ –±”©–≥–ª”©–Ω”© “Ø“Ø.");
      return;
    }
    if (!email.includes("@") || !email.includes(".")) {
      setError("–ò–º—ç–π–ª —Ö–∞—è–≥–∞–∞ –∑”©–≤ —Ñ–æ—Ä–º–∞—Ç—Ç–∞–π –æ—Ä—É—É–ª–Ω–∞ —É—É.");
      return;
    }
    if (!/^\d{4}$/.test(pin)) {
