    const { data: existing, error: existsError } = await supabase
      .from("users")
      .select("id")
      .or(`phone.eq.${phone},email.eq.${email}`)
      .maybeSingle();

    if (existsError) {
      console.error(existsError);
      setIsSubmitting(false);
      setError("–°–µ—Ä–≤–µ—Ä–∏–π–Ω –∞–ª–¥–∞–∞ –≥–∞—Ä–ª–∞–∞. –î–∞—Ä–∞–∞ –¥–∞—Ö–∏–Ω –æ—Ä–æ–ª–¥–æ–Ω–æ —É—É.");
      return;
    }

    if (existing) {
